import styled from 'styled-components'
export const InputContainer = styled.div`
  width: 100%;
  height: 75px;
  background-color: #282828

  display: flex;
  align-items: center;
  justify-content: flex-end;

  font-size: 24px;
  font-family: Arial, Helvetica, sans-serif;

  input {
    width: 100%;
    height: 75px;
    background-color: #aaffaa;
  }
`
