import styled from 'styled-components'

export const ButtonContainer = styled.button`
  border: 1px solid cyan;
  padding: 1rem;
  margin: 1rem;
  width: 120px;
  display: flex;

  background-color: grey;

  button {
    background-color: grey;
    width: 100%;
    border: none;
  }
`
