import styled from 'styled-components'

export const ButtonContainer = styled.button`
  border: 1px solid cyan;
  padding: 1rem;
  margin: 1rem;
  width: 120px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 5px;

  flex: 1;

  background-color: grey;

  &:hover {
    background-color: cyan;
  }
`
